Movie.create!({:genres=>["Drama", "Crime"], :title=>"The Godfather", :overview=>"The story spans the years from 1945 to 1955 and chronicles the fictional Italian-American Corleone crime family. When organized crime family patriarch Vito Corleone barely survives an attempt on his life, his youngest son, Michael, steps in to take care of the would-be killers, launching a campaign of bloody revenge.", :poster_path=>"https://image.tmdb.org/t/p/w185/d4KNaTrltq6bpkFS01pYtyXa09m.jpg", :release_date=>"1972-03-15"})
Movie.create!({:genres=>["Romance", "Comedy", "Animation", "Drama", "Family"], :title=>"Feast", :overview=>"This Oscar-winning animated short film tells the story of one man's love life is seen through the eyes of his best friend and dog, Winston, and revealed bite by bite through the meals they share.", :poster_path=>"https://image.tmdb.org/t/p/w185/qJ9ouIj4wN24asvKTUSAcJnSfrT.jpg", :release_date=>"2014-11-07"})
Movie.create!({:genres=>["Drama", "Comedy"], :title=>"The Intouchables", :overview=>"A true story of two men who should never have met - a quadriplegic aristocrat who was injured in a paragliding accident and a young man from the projects.", :poster_path=>"https://image.tmdb.org/t/p/w185/4mFsNQwbD0F237Tx7gAPotd0nbJ.jpg", :release_date=>"2011-11-02"})
Movie.create!({:genres=>["Science Fiction", "Fantasy", "Adventure"], :title=>"Guardians of the Galaxy", :overview=>"Light years from Earth, 26 years after being abducted, Peter Quill finds himself the prime target of a manhunt after discovering an orb wanted by Ronan the Accuser.", :poster_path=>"https://image.tmdb.org/t/p/w185/y31QB9kn3XSudA15tV7UWQ9XLuW.jpg", :release_date=>"2014-08-01"})
Movie.create!({:genres=>["Comedy", "Animation", "Family"], :title=>"Inside Out", :overview=>"Growing up can be a bumpy road, and it's no exception for Riley, who is uprooted from her Midwest life when her father starts a new job in San Francisco. Like all of us, Riley is guided by her emotions - Joy, Fear, Anger, Disgust and Sadness. The emotions live in Headquarters, the control center inside Riley's mind, where they help advise her through everyday life. As Riley and her emotions struggle to adjust to a new life in San Francisco, turmoil ensues in Headquarters. Although Joy, Riley's main and most important emotion, tries to keep things positive, the emotions conflict on how best to navigate a new city, house and school.", :poster_path=>"https://image.tmdb.org/t/p/w185/aAmfIX3TT40zUHGcCKrlOZRKC7u.jpg", :release_date=>"2015-06-19"})
Movie.create!({:genres=>["Fantasy", "Adventure", "Animation", "Family"], :title=>"Spirited Away", :overview=>"Spirited Away is an Oscar winning Japanese animated film about a ten year old girl who wanders away from her parents along a path that leads to a world ruled by strange and unusual monster-like animals. Her parents have been changed into pigs along with others inside a bathhouse full of these creatures. Will she ever see the world how it once was?", :poster_path=>"https://image.tmdb.org/t/p/w185/dL11DBPcRhWWnJcFXl9A07MrqTI.jpg", :release_date=>"2001-07-20"})
Movie.create!({:genres=>["Comedy", "Drama"], :title=>"Life Is Beautiful", :overview=>"A touching story of an Italian book seller of Jewish ancestry who lives in his own little fairy tale. His creative and happy life would come to an abrupt halt when his entire family is deported to a concentration camp during World War II. While locked up he tries to convince his son that the whole thing is just a game.", :poster_path=>"https://image.tmdb.org/t/p/w185/f7DImXDebOs148U4uPjI61iDvaK.jpg", :release_date=>"1997-12-20"})
Movie.create!({:genres=>["Drama"], :title=>"12 Angry Men", :overview=>"The defense and the prosecution have rested and the jury is filing into the jury room to decide if a young Spanish-American is guilty or innocent of murdering his father. What begins as an open and shut case soon becomes a mini-drama of each of the jurors' prejudices and preconceptions about the trial, the accused, and each other.", :poster_path=>"https://image.tmdb.org/t/p/w185/qcL1YfkCxfhsdO6sDDJ0PpzMF9n.jpg", :release_date=>"1957-04-10"})
Movie.create!({:genres=>["Animation", "Family", "Fantasy"], :title=>"Song of the Sea", :overview=>"The story of the last Seal Child’s journey home. After their mother’s disappearance, Ben and Saoirse are sent to live with Granny in the city. When they resolve to return to their home by the sea, their journey becomes a race against time as they are drawn into a world Ben knows only from his mother’s folktales. But this is no bedtime story; these fairy folk have been in our world far too long. It soon becomes clear to Ben that Saoirse is the key to their survival.", :poster_path=>"https://image.tmdb.org/t/p/w185/uvNv23Arf2ZYtimiStSB2c1DAEX.jpg", :release_date=>"2014-09-06"})
Movie.create!({:genres=>["Family", "Animation", "War", "Drama"], :title=>"Grave of the Fireflies", :overview=>"In the latter part of World War II, a boy and his sister, orphaned when their mother is killed in the firebombing of Tokyo, are left to survive on their own in what remains of civilian life in Japan. The plot follows this boy and his sister as they do their best to survive in the Japanese countryside, battling hunger, prejudice, and pride in their own quiet, personal battle.", :poster_path=>"https://image.tmdb.org/t/p/w185/bwVhmPpydv8P7mWfrmL3XVw0MV5.jpg", :release_date=>"1988-04-16"})
Movie.create!({:genres=>["History", "Drama", "Thriller", "War"], :title=>"The Imitation Game", :overview=>"Based on the real life story of legendary cryptanalyst Alan Turing, the film portrays the nail-biting race against time by Turing and his brilliant team of code-breakers at Britain's top-secret Government Code and Cypher School at Bletchley Park, during the darkest days of World War II.", :poster_path=>"https://image.tmdb.org/t/p/w185/noUp0XOqIcmgefRnRZa1nhtRvWO.jpg", :release_date=>"2014-11-14"})
Movie.create!({:genres=>["Drama", "Crime"], :title=>"The Godfather: Part II", :overview=>"The continuing saga of the Corleone crime family tells the story of a young Vito Corleone growing up in Sicily and in 1910s New York; and follows Michael Corleone in the 1950s as he attempts to expand the family business into Las Vegas, Hollywood and Cuba", :poster_path=>"https://image.tmdb.org/t/p/w185/tHbMIIF51rguMNSastqoQwR0sBs.jpg", :release_date=>"1974-12-20"})
Movie.create!({:genres=>["Mystery", "Thriller", "Drama"], :title=>"Gone Girl", :overview=>"With his wife's disappearance having become the focus of an intense media circus, a man sees the spotlight turned on him when it's suspected that he may not be innocent.", :poster_path=>"https://image.tmdb.org/t/p/w185/gdiLTof3rbPDAmPaCf4g6op46bj.jpg", :release_date=>"2014-10-03"})
Movie.create!({:genres=>["Adventure", "Fantasy", "Animation"], :title=>"Princess Mononoke", :overview=>"Ashitaka, a prince of the disappearing Ainu tribe, is cursed by a demonized boar god and must journey to the west to find a cure. Along the way, he encounters San, a young human woman fighting to protect the forest, and Lady Eboshi, who is trying to destroy it. Ashitaka must find a way to bring balance to this conflict.", :poster_path=>"https://image.tmdb.org/t/p/w185/gzlJkVfWV5VEG5xK25cvFGJgkDz.jpg", :release_date=>"1997-07-12"})
