Movie.create!({:genres=>["Action", "Drama", "War"], :title=>"Band of Brothers", :overview=>"Drawn from interviews with survivors of Easy Company, as well as their journals and letters, Band of Brothers chronicles the experiences of these men from paratrooper training in Georgia through the end of the war. As an elite rifle company parachuting into Normandy early on D-Day morning, participants in the Battle of the Bulge, and witness to the horrors of war, the men of Easy knew extraordinary bravery and extraordinary fear - and became the stuff of legend. Based on Stephen E. Ambrose's acclaimed book of the same name.", :poster_path=>"https://image.tmdb.org/t/p/w185/yRXTVpDRBA3983C3HjoY0SO4dV6.jpg", :release_date=>"2001-09-09"})
Movie.create!({:genres=>["Drama"], :title=>"Whiplash", :overview=>"Under the direction of a ruthless instructor, a talented young drummer begins to pursue perfection at any cost, even his humanity.", :poster_path=>"https://image.tmdb.org/t/p/w185/lIv1QinFqz4dlp5U4lQ6HaiskOZ.jpg", :release_date=>"2014-10-10"})
Movie.create!({:genres=>["Drama", "Comedy"], :title=>"Bajrangi Bhaijaan", :overview=>"A young mute girl from Pakistan loses herself in India with no way to head back. A devoted man with a magnanimous spirit undertakes the task to get her back to her motherland and unite her with her family.", :poster_path=>"https://image.tmdb.org/t/p/w185/71dIayQVmWw2QEs4AJFpqdv2n0S.jpg", :release_date=>"2015-07-17"})
Movie.create!({:genres=>["Drama", "Science Fiction"], :title=>"Interstellar", :overview=>"Interstellar chronicles the adventures of a group of explorers who make use of a newly discovered wormhole to surpass the limitations on human space travel and conquer the vast distances involved in an interstellar voyage.", :poster_path=>"https://image.tmdb.org/t/p/w185/nBNZadXqJSdt05SHLqgT0HuC5Gm.jpg", :release_date=>"2014-11-05"})
Movie.create!({:genres=>["Animation", "Romance", "Family"], :title=>"Paperman", :overview=>"An urban office worker finds that paper airplanes are instrumental in meeting a girl in ways he never expected.", :poster_path=>"https://image.tmdb.org/t/p/w185/3TpMBcAYH4cxCw5WoRacWodMTCG.jpg", :release_date=>"2012-11-02"})
Movie.create!({:genres=>["Animation", "Family"], :title=>"Presto", :overview=>"Dignity. Poise. Mystery. We expect nothing less from the great turn-of-the-century magician, Presto. But when Presto neglects to feed his rabbit one too many times, the magician finds he isn't the only one with a few tricks up his sleeve!", :poster_path=>"https://image.tmdb.org/t/p/w185/A2rxR8g3y6kcjIoR2fcwtq9eppc.jpg", :release_date=>"2008-06-26"})
Movie.create!({:genres=>["Drama", "Crime"], :title=>"The Shawshank Redemption", :overview=>"Framed in the 1940s for the double murder of his wife and her lover, upstanding banker Andy Dufresne begins a new life at the Shawshank prison, where he puts his accounting skills to work for an amoral warden. During his long stretch in prison, Dufresne comes to be admired by the other inmates -- including an older prisoner named Red -- for his integrity and unquenchable sense of hope.", :poster_path=>"https://image.tmdb.org/t/p/w185/9O7gLzmreU0nGkIB6K3BsJbzvNv.jpg", :release_date=>"1994-09-14"})
Movie.create!({:genres=>["Animation", "Comedy", "Family"], :title=>"Inside Out", :overview=>"Growing up can be a bumpy road, and it's no exception for Riley, who is uprooted from her Midwest life when her father starts a new job in San Francisco. Like all of us, Riley is guided by her emotions - Joy, Fear, Anger, Disgust and Sadness. The emotions live in Headquarters, the control center inside Riley's mind, where they help advise her through everyday life. As Riley and her emotions struggle to adjust to a new life in San Francisco, turmoil ensues in Headquarters. Although Joy, Riley's main and most important emotion, tries to keep things positive, the emotions conflict on how best to navigate a new city, house and school.", :poster_path=>"https://image.tmdb.org/t/p/w185/aAmfIX3TT40zUHGcCKrlOZRKC7u.jpg", :release_date=>"2015-06-19"})
Movie.create!({:genres=>["Drama", "Crime"], :title=>"The Godfather", :overview=>"The story spans the years from 1945 to 1955 and chronicles the fictional Italian-American Corleone crime family. When organized crime family patriarch Vito Corleone barely survives an attempt on his life, his youngest son, Michael, steps in to take care of the would-be killers, launching a campaign of bloody revenge.", :poster_path=>"https://image.tmdb.org/t/p/w185/d4KNaTrltq6bpkFS01pYtyXa09m.jpg", :release_date=>"1972-03-15"})
Movie.create!({:genres=>["Adventure", "Fantasy", "Science Fiction"], :title=>"Guardians of the Galaxy", :overview=>"Light years from Earth, 26 years after being abducted, Peter Quill finds himself the prime target of a manhunt after discovering an orb wanted by Ronan the Accuser.", :poster_path=>"https://image.tmdb.org/t/p/w185/y31QB9kn3XSudA15tV7UWQ9XLuW.jpg", :release_date=>"2014-08-01"})
Movie.create!({:genres=>["Drama"], :title=>"12 Angry Men", :overview=>"The defense and the prosecution have rested and the jury is filing into the jury room to decide if a young Spanish-American is guilty or innocent of murdering his father. What begins as an open and shut case soon becomes a mini-drama of each of the jurors' prejudices and preconceptions about the trial, the accused, and each other.", :poster_path=>"https://image.tmdb.org/t/p/w185/qcL1YfkCxfhsdO6sDDJ0PpzMF9n.jpg", :release_date=>"1957-04-10"})
Movie.create!({:genres=>["Drama", "Music"], :title=>"Straight Outta Compton", :overview=>"In 1987, five young men, using brutally honest rhymes and hardcore beats, put their frustration and anger about life in the most dangerous place in America into the most powerful weapon they had: their music. Taking us back to where it all began, Straight Outta Compton tells the true story of how these cultural rebels-armed only with their lyrics, swagger, bravado and raw talent-stood up to the authorities that meant to keep them down and formed the world's most dangerous group, N.W.A. And as they spoke the truth that no one had before and exposed life in the hood, their voice ignited a social revolution that is still reverberating today. Straight Outta Compton stars O’Shea Jackson Jr., Corey Hawkins and Jason Mitchell as Ice Cube, Dr. Dre and Eazy-E, and is directed by F. Gary Gray.", :poster_path=>"https://image.tmdb.org/t/p/w185/X7S1RtotXOZNV7OlgCfh5VKZSB.jpg", :release_date=>"2015-08-14"})
Movie.create!({:genres=>["Animation", "Family", "Fantasy"], :title=>"Song of the Sea", :overview=>"The story of the last Seal Child’s journey home. After their mother’s disappearance, Ben and Saoirse are sent to live with Granny in the city. When they resolve to return to their home by the sea, their journey becomes a race against time as they are drawn into a world Ben knows only from his mother’s folktales. But this is no bedtime story; these fairy folk have been in our world far too long. It soon becomes clear to Ben that Saoirse is the key to their survival.", :poster_path=>"https://image.tmdb.org/t/p/w185/uvNv23Arf2ZYtimiStSB2c1DAEX.jpg", :release_date=>"2014-12-19"})
Movie.create!({:genres=>["Comedy", "Drama"], :title=>"The Intouchables", :overview=>"A true story of two men who should never have met - a quadriplegic aristocrat who was injured in a paragliding accident and a young man from the projects.", :poster_path=>"https://image.tmdb.org/t/p/w185/4mFsNQwbD0F237Tx7gAPotd0nbJ.jpg", :release_date=>"2011-11-02"})
Movie.create!({:genres=>["Romance", "Comedy", "Animation", "Drama", "Family"], :title=>"Feast", :overview=>"This Oscar-winning animated short film tells the story of one man's love life is seen through the eyes of his best friend and dog, Winston, and revealed bite by bite through the meals they share.", :poster_path=>"https://image.tmdb.org/t/p/w185/qJ9ouIj4wN24asvKTUSAcJnSfrT.jpg", :release_date=>"2014-11-07"})
Movie.create!({:genres=>["Animation", "Adventure", "Comedy", "Family", "Fantasy"], :title=>"Partly Cloudy", :overview=>"Everyone knows that the stork delivers babies, but where do the storks get the babies from? The answer lies up in the stratosphere, where cloud people sculpt babies from clouds and bring them to life. Gus, a lonely and insecure grey cloud, is a master at creating \"dangerous\" babies. Crocodiles, porcupines, rams and more - Gus's beloved creations are works of art, but more than a handful for his loyal delivery stork partner, Peck. As Gus's creations become more and more rambunctious, Peck's job gets harder and harder. How will Peck manage to handle both his hazardous cargo and his friend's fiery temperament?", :poster_path=>"https://image.tmdb.org/t/p/w185/5M5bg79OV96Vb4O0fDjX5clxASG.jpg", :release_date=>"2009-05-28"})
Movie.create!({:genres=>["Drama", "History", "Thriller", "War"], :title=>"The Imitation Game", :overview=>"Based on the real life story of legendary cryptanalyst Alan Turing, the film portrays the nail-biting race against time by Turing and his brilliant team of code-breakers at Britain's top-secret Government Code and Cypher School at Bletchley Park, during the darkest days of World War II.", :poster_path=>"https://image.tmdb.org/t/p/w185/noUp0XOqIcmgefRnRZa1nhtRvWO.jpg", :release_date=>"2014-11-14"})
Movie.create!({:genres=>["Fantasy", "Adventure", "Animation", "Family"], :title=>"Spirited Away", :overview=>"Spirited Away is an Oscar winning Japanese animated film about a ten year old girl who wanders away from her parents along a path that leads to a world ruled by strange and unusual monster-like animals. Her parents have been changed into pigs along with others inside a bathhouse full of these creatures. Will she ever see the world how it once was?", :poster_path=>"https://image.tmdb.org/t/p/w185/dL11DBPcRhWWnJcFXl9A07MrqTI.jpg", :release_date=>"2001-07-20"})
Movie.create!({:genres=>["Animation", "Fantasy", "Family", "Drama"], :title=>"Wolf Children", :overview=>"Hana, a nineteen-year-old college student, falls in love with a man only for him to reveal his secret; he is a Wolf Man. Eventually the couple bear two children together; a son and daughter they name Ame and Yuki who both inherit the ability to transform into wolves from their father. When the man Hana fell in love with suddenly dies, she makes the decision to move to a rural town isolated from society to continue raising the children in protection.", :poster_path=>"https://image.tmdb.org/t/p/w185/rDMxjCYEVnvLC4nsBpB6wjL0LDy.jpg", :release_date=>"2012-08-29"})
Movie.create!({:genres=>["Comedy", "Drama"], :title=>"Life Is Beautiful", :overview=>"A touching story of an Italian book seller of Jewish ancestry who lives in his own little fairy tale. His creative and happy life would come to an abrupt halt when his entire family is deported to a concentration camp during World War II. While locked up he tries to convince his son that the whole thing is just a game.", :poster_path=>"https://image.tmdb.org/t/p/w185/f7DImXDebOs148U4uPjI61iDvaK.jpg", :release_date=>"1997-12-20"})
